package com.microsoft.tfs.core.clients.build.internal;

import com.microsoft.tfs.core.clients.build.internal.utils.BuildTypeUtil;

/**
 * Build info class generated by {@link BuildTypeUtil}.
 */
public class BuildTypeInfo {
    private String description;
    private String buildMachine;
    private String dropLocation;
    private String buildDir;
    private String name;
    private String version;

    public BuildTypeInfo(
        final String name,
        final String description,
        final String buildMachine,
        final String buildDir,
        final String dropLocation,
        final String version) {
        this.name = name;
        this.description = description;
        this.buildMachine = buildMachine;
        this.buildDir = buildDir;
        this.dropLocation = dropLocation;
        this.version = version;
    }

    public BuildTypeInfo(final String name) {
        this(name, "", "", "", "", ""); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$
    }

    /**
     * @return the description
     */
    public String getDescription() {
        return description;
    }

    /**
     * @param description
     *        the description to set
     */
    public void setDescription(final String description) {
        this.description = description;
    }

    /**
     * @return the buildMachine
     */
    public String getBuildMachine() {
        return buildMachine;
    }

    /**
     * @param buildMachine
     *        the buildMachine to set
     */
    public void setBuildMachine(final String buildMachine) {
        this.buildMachine = buildMachine;
    }

    /**
     * @return the dropLocation
     */
    public String getDropLocation() {
        return dropLocation;
    }

    /**
     * @param dropLocation
     *        the dropLocation to set
     */
    public void setDropLocation(final String dropLocation) {
        this.dropLocation = dropLocation;
    }

    /**
     * @return the buildDir
     */
    public String getBuildDir() {
        return buildDir;
    }

    /**
     * @param buildDir
     *        the buildDir to set
     */
    public void setBuildDir(final String buildDir) {
        this.buildDir = buildDir;
    }

    /**
     * @return the name
     */
    public String getName() {
        return name;
    }

    /**
     * @param name
     *        the name to set
     */
    public void setName(final String name) {
        this.name = name;
    }

    /**
     * @return the version
     */
    public String getVersion() {
        return version;
    }

    /**
     * @param version
     *        the version to set
     */
    public void setVersion(final String version) {
        this.version = version;
    }

}
